import axios from 'axios';
export default {
  name: 'BinaryUpload',
  data() {
    return {
      selectedFile: null,
      loading: false
    };
  },
  computed: {
    valid() {
      return !!this.selectedFile;
    },
    fileRules() {
      return [value => {
        if (!value) {
          return 'File is required';
        } else if (!value.name.endsWith('.bin')) {
          return 'Only binary files are allowed';
        }
        return true;
      }];
    }
  },
  methods: {
    onSubmit() {
      const formData = new FormData();
      formData.append('binary', this.selectedFile);
      this.loading = true;
      axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        console.log(response.data);
      }).catch(error => {
        console.error(error);
      }).finally(() => {
        this.loading = false;
        this.$refs.form.reset();
      });
    }
  }
};
