import axios from 'axios';
export default {
  name: 'BinaryUpload',
  data() {
    return {
      selectedFile: null,
      loading: false
    };
  },
  computed: {
    valid() {
      return !!this.selectedFile;
    },
    fileRules() {
      return [value => {
        if (!value) {
          return 'File is required';
        }
        return true;
      }];
    }
  },
  methods: {
    //collectorImport 是封装的ajax方法，不管用什么ajax请求方法，主要是设置请求头'Content-Type': 'multipart/form-data'
    collectorImport(data) {
      return request({
        url: 'http://127.0.0.1:5000/upload_file',
        method: 'post',
        data,
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        console.log(response.data);
      }).catch(error => {
        console.error(error);
      });
    },
    // 上传文件方法
    uploadFile() {
      console.log(this.fileInfo, '文件信息');
      if (this.$refs.uploadFileForm.validate()) {
        this.loading.uploadIsLoading = true;
        var formData = new window.FormData();
        formData.append('file', this.fileInfo);
        collectorImport(formData).then(res => {
          this.loading.uploadIsLoading = false;
          this.$refs.notify.show("文件上传成功", {
            timeout: 1000,
            color: 'success'
          });
          this.uploadDialog = false;
          this.search();
        }).catch(err => {
          this.loading.uploadIsLoading = false;
        });
      }
    },
    onSubmit() {
      const formData = new FormData();
      formData.append('binary', this.selectedFile);
      console.log(formData);
      this.loading = true;
      axios.post('http://127.0.0.1:5000/upload_file', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        console.log(response.data);
      }).catch(error => {
        console.error(error);
      }).finally(() => {
        this.loading = false;
        this.$refs.form.reset();
      });
    }
  }
};
